mapper.select:
SELECT count(student_id) FROM STUDENT WHERE 1=1
AND school_type = #{gubun}
AND student_name LIKE CONCAT (#{studentName}, '%')

SELECT * FROM STUDENT
WHERE 1=1

	AND school_type = #{gubun}


	AND student_name LIKE CONCAT (#{studentName}, '%')

LIMIT #{start}, #{end}

SELECT count(A.student_memo_id)
FROM STUDENT_MEMO A LEFT JOIN FLOWEDU_MEMBER B
ON A.flow_member_id = B.flow_member_id
WHERE 1=1

	AND A.student_id = #{studentId}


	AND DATE_FORMAT(A.create_date, '%Y-%m') = #{searchDate}


	AND A.memo_type = #{memoType}


	AND B.member_name LIKE CONCAT ('%', #{memberName}, '%')


	AND A.memo_content LIKE CONCAT ('%', #{memoContent}, '%')

SELECT A.*, B.member_name,
(SELECT count(student_memo_id) FROM STUDENT_MEMO_REPLY WHERE student_memo_id = A.student_memo_id AND delete_yn = 0) as reply_count
FROM STUDENT_MEMO A LEFT JOIN FLOWEDU_MEMBER B
ON A.flow_member_id = B.flow_member_id
WHERE 1=1

	AND student_id = #{studentId}


	AND DATE_FORMAT(A.create_date, '%Y-%m') = #{searchDate}


	AND A.memo_type = #{memoType}


	AND B.member_name LIKE CONCAT ('%', #{memberName}, '%')


	AND A.memo_content LIKE CONCAT ('%', #{memoContent}, '%')

ORDER BY A.create_date DESC
LIMIT #{start}, #{end}

SELECT A.*, B.member_name FROM STUDENT_MEMO_REPLY A LEFT JOIN FLOWEDU_MEMBER B ON A.flow_member_id = B.flow_member_id WHERE 1=1
AND student_memo_id = #{studentMemoId}
AND delete_yn = 0 LIMIT #{start}, #{end}

mapper.update:
UPDATE STUDENT
student_name = #{studentName}, student_password = #{studentPassword}, student_gender = #{studentGender}, student_birthday = #{studentBirthday}, home_tel_number = #{homeTelNumber}, student_phone_number = #{studentPhoneNumber}, student_email = #{studentEmail}, school_name = #{schoolName}, school_type = #{schoolType}, student_grade = #{studentGrade}, student_photo_file = #{studentPhotoFile}, student_photo_url = #{studentPhotoUrl}, student_memo = #{studentMemo}, mother_name = #{motherName}, mother_phone_number = #{motherPhoneNumber}, father_name = #{fatherName}, father_phone_number = #{fatherPhoneNumber}, etc_name = #{etcName}, etc_phone_number = #{etcPhoneNumber},
student_id = #{studentId}

UPDATE STUDENT_MEMO
SET process_yn = #{processYn}
WHERE student_memo_id = #{studentMemoId}

UPDATE STUDENT_MEMO_REPLY

	reply_content = #{replyContent},
	delete_yn = #{deleteYn}

WHERE student_memo_reply_id = #{studentMemoReplyId}
